$(document).ready(function(){$("#editTag").submit(function(n){n.preventDefault();var t={Id:$("#id").val(),Name:$("#name").val(),DisplayName:$("#displayName").val()};$.ajax({url:$(this).attr("action"),type:"POST",data:t,success:function(){$("#successMessage").fadeIn().delay(1e3).fadeOut()},error:function(){alert("An error occurred while saving changes.")}})});$(document).ready(function(){$("#btnDelete").click(function(n){n.preventDefault();var t=$("#id").val();Swal.fire({title:"Are you sure you want to delete this tag?",icon:"warning",showCancelButton:!0,confirmButtonText:"Yes, delete it!",cancelButtonText:"Cancel"}).then(n=>{n.value&&$.post("/AdminTags/Delete",{id:t},function(n){n.success?(Swal.fire("Tag deleted successfully!","","success"),window.location.href="/AdminTags/List"):window.location.href="/AdminTags/List"})})})})});